function requestStatisticsData(){$.ajax({url:gender_statistics_url,success:function(e){var t=$("#gender").highcharts();t.xAxis[0].setCategories(e.categories,!1),t.series[0].setData(e.female,!1),t.series[1].setData(e.male,!1),t.series[2].setData(e.other,!1),t.redraw(),setTimeout(requestStatisticsData,2e3)},cache:!1})}var chart_gender;$(document).ready(function(){chart_gender=new Highcharts.Chart({chart:{renderTo:"gender",defaultSeriesType:"bar",backgroundColor:"#fff",events:{load:requestStatisticsData}},title:!1,xAxis:{categories:[]},yAxis:{min:0,title:{text:"Gender Statistics"},stackLabels:{enabled:!0,style:{fontWeight:"bold",color:Highcharts.theme&&Highcharts.theme.textColor||"gray"}}},credits:{enabled:!1},plotOptions:{bar:{stacking:"percent",dataLabels:{enabled:!1,color:Highcharts.theme&&Highcharts.theme.dataLabelsColor||"white",style:{textShadow:"0 0 3px black"}}}},legend:{align:"right",x:-30,verticalAlign:"top",y:25,floating:!0,backgroundColor:Highcharts.theme&&Highcharts.theme.background2||"white",borderColor:"#CCC",borderWidth:1,shadow:!1},tooltip:{formatter:function(){return"<b>"+this.x+"</b><br/>"+this.series.name+": "+this.y+"<br/>Total: "+this.point.stackTotal}},series:[{name:"Female",data:[],color:"#428FCD"},{name:"Male",data:[],color:"#2E4496"},{name:"Other",data:[],color:"#FEEB36"}]})});