function addInput(t){if(counter_subtopics==limit_subtopics)alert("More than 10 subtopics is just confusing!");else{var s=document.createElement("div");s.innerHTML="<label class='control-label col-sm-2' for='inputLarge'></label><div class='col-sm-8'><input class='form-control input-sm' type='text' id='subtopic' value=''></div><div class='col-sm-2'><span class='input-group-btn'><button class='btn btn-primary btn-sm' type='button'>Delete</button></span></div>",document.getElementById(t).appendChild(s),counter_subtopics++}}var counter_subtopics=1,limit_subtopics=10;